import { ServerOptions, PluginOption } from 'vite';

declare type HttpsOption = ServerOptions['https'];
interface ResolvedConfig {
    additionalEntrypoints: string[];
    assetHost: string;
    assetsDir: string;
    configPath: string;
    publicDir: string;
    mode: string;
    entrypointsDir: string;
    sourceCodeDir: string;
    host: string;
    https: HttpsOption;
    port: number;
    publicOutputDir: string;
    watchAdditionalPaths: string[];
}
declare type Config = Partial<ResolvedConfig>;
interface PluginOptions {
    root: string;
    outDir: string;
    base: string;
}
declare type Entrypoints = Array<[string, string]>;
declare type UnifiedConfig = ResolvedConfig & PluginOptions & {
    entrypoints: Entrypoints;
};
declare type MultiEnvConfig = Record<string, Config | undefined>;

declare const projectRoot: string;
declare function ViteRubyPlugin(): PluginOption[];

export default ViteRubyPlugin;
export { Config, Entrypoints, HttpsOption, MultiEnvConfig, PluginOptions, ResolvedConfig, UnifiedConfig, projectRoot };
